```kotlin:app/src/main/java/dev/eknath/barathtrainbookingremainder/MainActivity.kt
NavHost(
 navController = navController,
 startDestination = "home",
 modifier = Modifier.padding(innerPadding)
) {
 // Your existing routes

 composable("home") {
 HomeScreen(navController = navController, viewModel = reminderViewModel)
 }

 composable("calendar") {
 CalendarScreen(
 navigateBack = { navController.popBackStack() },
 navController = navController
 )
 }

 // Add reminder form with date parameters
 composable(
 route = "add_reminder?date={date}&bookableDate={bookableDate}",
 arguments = listOf(
 navArgument("date") { type = NavType.StringType },
 navArgument("bookableDate") { type = NavType.StringType }
 )
 ) { backStackEntry ->
 val dateString = backStackEntry.arguments?.getString("date") ?: ""
 val bookableDateString = backStackEntry.arguments?.getString("bookableDate") ?: ""

 // Parse the dates
 val selectedDate = if (dateString.isNotEmpty()) 
 LocalDate.parse(dateString) 
 else 
 LocalDate.now().plusDays(60)

 val bookableDate = if (bookableDateString.isNotEmpty()) 
 LocalDate.parse(bookableDateString) 
 else 
 selectedDate.plusDays(90)

 AddReminderScreen(
 navController = navController,
 viewModel = reminderViewModel,
 preselectedDate = selectedDate,
 preselectedBookableDate = bookableDate
 )
 }

 // Other routes...
}
```

